package com.shen.keep.app.fragment;import android.content.Intent;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.TextView;import com.shen.keep.R;import com.shen.keep.app.activity.HomeActivity;import com.shen.keep.core.base.BaseFragment;import butterknife.Bind;import butterknife.ButterKnife;import butterknife.OnClick;/** * Created by edianzu on 2016/9/2. */public class HomeTabFragment extends BaseFragment {    @Bind(R.id.keep_sum_time_tv)    TextView mKeepSumTimeTv;    @Bind(R.id.keep_sum_time_hint)    TextView mKeepSumTimeHint;    @Bind(R.id.keep_name_tv)    TextView mKeepNameTv;    @Bind(R.id.start_keep_btn)    Button mStartKeepBtn;    @Bind(R.id.query_keep_btn)    Button mQueryKeepBtn;    public static HomeTabFragment newInstance(int index) {        HomeTabFragment homeTabFragment = new HomeTabFragment();        Bundle bundle = new Bundle();        bundle.putInt("index", index);        homeTabFragment.setArguments(bundle);        return homeTabFragment;    }    @Override    public void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);    }    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        mView = inflater.inflate(R.layout.fragment_home, container, false);        super.onCreateView(inflater, container, savedInstanceState);        ButterKnife.bind(this, mView);        int index = (int) getArguments().get("index");        mKeepSumTimeTv.setText(String.valueOf(index));        return mView;    }    @Override    public void onResume() {        super.onResume();    }    @Override    public void onPause() {        super.onPause();    }    @Override    public void onDestroy() {        super.onDestroy();    }    @Override    public void clearObject() {    }    @Override    public void clearView() {    }    @Override    public void onDestroyView() {        super.onDestroyView();        ButterKnife.unbind(this);    }    @OnClick(R.id.start_keep_btn)    public void onClick() {        Intent intent = new Intent(mContext, HomeActivity.class);        startActivity(intent);    }}