package com.shen.keep.app.fragment;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.shen.keep.R;import com.shen.keep.core.base.BaseFragment;import butterknife.ButterKnife;/** * Created by edianzu on 2016/9/2. */public class MyTabFragment extends BaseFragment {    public static MyTabFragment newInstance(int index) {        MyTabFragment homeTabFragment = new MyTabFragment();        Bundle bundle = new Bundle();        bundle.putInt("index", index);        homeTabFragment.setArguments(bundle);        return homeTabFragment;    }    @Override    public void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);    }    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        mView = inflater.inflate(R.layout.fragment_my, container, false);        super.onCreateView(inflater, container, savedInstanceState);        ButterKnife.bind(this, mView);        return mView;    }    @Override    public void onResume() {        super.onResume();    }    @Override    public void onPause() {        super.onPause();    }    @Override    public void clearObject() {    }    @Override    public void clearView() {    }    @Override    public void onDestroyView() {        super.onDestroyView();        ButterKnife.unbind(this);    }}